// Validate and process input data
const requiredFields = ['brandName', 'email', 'industry'];
const missingFields = [];

for (const field of requiredFields) {
  if (!$input.first().json[field]) {
    missingFields.push(field);
  }
}

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test($input.first().json.email)) {
  throw new Error('Invalid email format');
}

// Sanitize inputs
const sanitizedData = {
  brandName: $input.first().json.brandName.trim().substring(0, 100),
  email: $input.first().json.email.toLowerCase().trim(),
  industry: $input.first().json.industry || 'General',
  website: $input.first().json.website || '',
  competitor: $input.first().json.competitor || '',
  primaryColor: $input.first().json.primaryColor || '#F5793B',
  targetAudience: $input.first().json.targetAudience || '',
  timezone: $input.first().json.timezone || 'America/New_York',
  timestamp: new Date().toISOString(),
  requestId: Math.random().toString(36).substring(7)
};

// Content moderation - banned terms
const bannedTerms = ['guarantee', 'cure', 'medical', 'financial advice', 'get rich'];
const combinedText = Object.values(sanitizedData).join(' ').toLowerCase();

for (const term of bannedTerms) {
  if (combinedText.includes(term)) {
    throw new Error(`Content policy violation: ${term} detected`);
  }
}

return [sanitizedData];
